import React, { useEffect, useState, useRef } from 'react';
import { motion, useAnimation } from 'framer-motion';

const starImages = [
  '/stars/1.png',
  '/stars/2.png',
  '/stars/3.png',
  '/stars/4.png',
  '/stars/5.png'
];

const compliments = [
  "1 Humble",
  "2 Sincerity",
  "3 Patient",
  "4 smart",
  "5 愈挫愈強"
];

const maskImageSrc = '/jar/spawn-mask.png';

const createMaskContext = () => {
  return new Promise((resolve) => {
    const img = new Image();
    img.crossOrigin = "anonymous";
    img.src = maskImageSrc;

    img.onload = () => {
      const canvas = document.createElement("canvas");
      canvas.width = 300;
      canvas.height = 300;
      const ctx = canvas.getContext("2d");
      ctx.drawImage(img, 0, 0, 300, 300);
      resolve(ctx);
    };

    img.onerror = () => {
      resolve(null);
    };
  });
};

const getRandomPositionFromMask = async () => {
  const ctx = await createMaskContext();
  if (!ctx) return { x: 150, y: 180 };

  const starSize = 32;
  const halfSize = starSize / 2;
  let attempts = 0;

  while (attempts < 1000) {
    const x = Math.floor(Math.random() * 300);
    const y = Math.floor(Math.random() * 300);

    const corners = [
      { x: x - halfSize, y: y - halfSize },
      { x: x + halfSize, y: y - halfSize },
      { x: x - halfSize, y: y + halfSize },
      { x: x + halfSize, y: y + halfSize }
    ];

    const allValid = corners.every(({ x, y }) => {
      if (x < 0 || x >= 300 || y < 0 || y >= 300) return false;
      const pixel = ctx.getImageData(x, y, 1, 1).data;
      return pixel[3] >= 50;
    });

    if (allValid) return { x, y };
    attempts++;
  }

  return { x: 150, y: 180 };
};

const Star = React.forwardRef(({ src, x, y, updateFinalPosition }, ref) => {
  const controls = useAnimation();
  let finalX = x;
  let finalY = y;

  React.useImperativeHandle(ref, () => ({
    async shake() {
      const ctx = await createMaskContext();
      if (!ctx) return;

      const starSize = 32;
      const halfSize = starSize / 2;

      for (let i = 0; i < 8; i++) {
        let newX = finalX + (Math.random() - 0.5) * 20;
        let newY = finalY + (Math.random() - 0.5) * 20;

        const corners = [
          { x: newX - halfSize, y: newY - halfSize },
          { x: newX + halfSize, y: newY - halfSize },
          { x: newX - halfSize, y: newY + halfSize },
          { x: newX + halfSize, y: newY + halfSize }
        ];

        const isValid = corners.every(({ x, y }) => {
          if (x < 0 || x >= 300 || y < 0 || y >= 300) return false;
          const pixel = ctx.getImageData(x, y, 1, 1).data;
          return pixel[3] >= 50;
        });

        if (!isValid) {
          await controls.start({
            x: finalX + (150 - finalX) * 0.05 - 16,
            y: finalY + (150 - finalY) * 0.05 - 16,
            rotate: -20,
            transition: { duration: 0.08, type: 'spring', bounce: 0.4 }
          });
          continue;
        }

        await controls.start({
          x: newX - 16,
          y: newY - 16,
          rotate: (Math.random() - 0.5) * 60,
          transition: { duration: 0.08 }
        });

        finalX = newX;
        finalY = newY;
      }

      await controls.start({
        x: finalX - 16,
        y: finalY - 16,
        rotate: 0,
        transition: { duration: 0.4, ease: "easeOut" }
      });

      updateFinalPosition(finalX, finalY);
    }
  }));

  return (
    <motion.img
      src={src}
      alt="star"
      className="absolute w-8 h-8 pointer-events-none"
      animate={controls}
      initial={{ x: x - 16, y: y - 16, scale: 1 }}
      style={{ zIndex: 5 }}
    />
  );
});

export default function StarJar() {
  const [stars, setStars] = useState([]);
  const [hasSpawned, setHasSpawned] = useState(false);
  const [wish, setWish] = useState("");
  const [isRevealing, setIsRevealing] = useState(false);
  const starRefs = useRef([]);

  const spawnStars = async () => {
    const generated = [];
    for (let i = 0; i < 5; i++) {
      const pos = await getRandomPositionFromMask();
      generated.push({
        id: `star-${i}`,
        src: starImages[i % starImages.length],
        x: pos.x,
        y: pos.y
      });
    }
    setStars(generated);
    setHasSpawned(true);
  };

  const updateStarPosition = (index, x, y) => {
    setStars(prev => {
      const updated = [...prev];
      updated[index] = { ...updated[index], x, y };
      return updated;
    });
  };

  const shakeStars = async () => {
    setWish("");
    setIsRevealing(true);
    await Promise.all(
      starRefs.current.map((ref) => ref?.shake?.())
    );
    setTimeout(() => {
      const randomWish = compliments[Math.floor(Math.random() * compliments.length)];
      setWish(randomWish);
      setIsRevealing(false);
    }, 1500);
  };

  return (
    <div className="flex flex-col items-center mt-10">
      <div className="relative w-[300px] h-[300px]">
        <img src="/jar/back.png" alt="jar back" className="absolute inset-0 w-full h-full object-contain z-0" />

        {stars.map((star, i) => (
          <Star
            key={star.id}
            src={star.src}
            x={star.x}
            y={star.y}
            ref={(el) => (starRefs.current[i] = el)}
            updateFinalPosition={(x, y) => updateStarPosition(i, x, y)}
          />
        ))}

        <img src="/jar/lid.png" alt="jar front" className="absolute inset-0 w-full h-full object-contain z-10" />
      </div>

      <motion.button
        className="mt-8 bg-gradient-to-r from-pink-300 to-purple-300 hover:from-pink-400 hover:to-purple-400 px-8 py-3 rounded-full shadow-lg text-white font-semibold text-lg"
        onClick={hasSpawned ? shakeStars : spawnStars}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        {hasSpawned ? "Make a wish! ✨" : "Press me!"}
      </motion.button>

      <div className="mt-4 text-center h-24 flex items-center justify-center">
        {isRevealing && (
          <img
            src="/animations/unravel.gif"
            alt="unraveling..."
            className="w-32 h-20 object-contain"
          />
        )}
        {!isRevealing && wish && (
          <p className="text-pink-700 text-xl font-semibold">✨ {wish} ✨</p>
        )}
      </div>
    </div>
  );
}
