    "14 fun",
    "15 reliable",
    "16 wise",
    "17 compliments",
    "18 kind",
    "19 ÊúâÈ¶ñÂ∞æ",
    "20 working on yourself",
    "21 kind 2.0"
  ];

  const [drawnCompliment, setDrawnCompliment] = useState(null);

  const drawStar = () => {
    const index = Math.floor(Math.random() * compliments.length);
    setDrawnCompliment(compliments[index]);
  };
  return (
    <div className="min-h-screen bg-yellow-50 flex flex-col items-center justify-center">
      <h1 className="text-4xl font-bold text-yellow-500 mb-6">üåü Âπ∏ÈÅãÊòü Jar begins! üåü</h1>

//ÊäΩÊòüÊòü button
      <motion.button
        whileTap={{ scale: 0.8, rotate: 10 }}
        whileHover={{ scale: 1.05 }}
        onClick={drawStar}
        className="bg-yellow-300 hover:bg-yellow-400 text-white font-bold py-3 px-6 rounded-full shadow-lg"
      >
        ÊäΩÂπ∏ÈÅãÊòü
      </motion.button>

      {drawnCompliment && (
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: [0, 1.2, 1], opacity: [0, 1] }}
          transition={{ duration: 0.5 }}
          className="text-xl mt-4"
        >
          ‚ú®‚ú®‚ú®
        </motion.div>
      )}

//compliment card
      <motion.div
        key={drawnCompliment}
        initial={{ opacity: 0, y: -80, rotate: -10 }}
        animate={{ opacity: 1, y: 0, rotate: 0 }}
        transition={{ duration: 0.6, type: "spring", stiffness: 100 }}
        className="text-3xl mb-4"
      >
        ‚≠êÔ∏è
      </motion.div>
