// ComplimentDex.js
import React, { useEffect, useState } from 'react';
import ComplimentModal from './ComplimentModal';

const compliments = [
    {
      text: '1 Humble',
      image: '/stars/pink.png',
      description: 'Humble and always hungry to learn. You always see the best in people and constantly look for ways to improve on yourself. I really look up to you for knowing where your weaknesses are and trying to work on these.',
    },
    {
      text: '2 Sincerity',
      image: '/stars/blue.png',
      description: 'You say things when you mean them. When you mistakenly said I love you due to mistranslation, you took it back without hurting my feelings or making it awkward, which only made me appreciate the gesture more.  ',
    },
    {
      text: '3 Patient',
      image: '/stars/purple.png',
      description: 'atient with me. When Iâ€™m not ready to try new things and Iâ€™m stuck in my own ways, you give me time, nudge me to grow out of my shell without pushing and wait for me.',
    },
    {
      text: '4 smart',
      image: '/stars/yellow.png',
      description: '4. Very crazily smart. You literally self learned machine learning in 2 weeks. What the hell??? Later add: you also learned everything in your 10 week course in 3 days. What the fuck.',
    },
    {
      text: '5 Charmer',
      image: '/stars/green.png',
      description: 'You are street smart. You know how to charm the room and is so charismatic. When you donâ€™t get carried away with your own yapping you yap wellðŸ‘ðŸ»',
    },
  ];

export default function ComplimentDex() {
  const [unlocked, setUnlocked] = useState([]);
  const [selected, setSelected] = useState(null);

  const loadUnlocked = () => {
    const saved = JSON.parse(localStorage.getItem('unlockedCompliments') || '[]');
    setUnlocked(saved);
  };

  useEffect(() => {
    loadUnlocked();
  }, []);

  return (
    <div className="px-4 py-6">
      <h2 className="text-center text-xl font-bold text-pink-700 mb-6">Compliment Dex</h2>
      <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-5 gap-4 justify-items-center">
        {compliments.map(({ text, image }, index) => {
          const isUnlocked = unlocked.includes(text);
          return (
            <div
              key={index}
              className="flex flex-col items-center text-center cursor-pointer"
              onClick={() => setSelected({ text, image, description, locked: !isUnlocked })}
            >
              <img
                src={image}
                alt={text}
                className={`w-12 h-12 sm:w-14 sm:h-14 md:w-16 md:h-16 transition-all duration-300 ${
                  isUnlocked ? '' : 'opacity-20 grayscale'
                }`}
              />
              <span className={`mt-1 text-xs sm:text-sm font-medium text-gray-400`}>
                {isUnlocked ? text : '???'}
              </span>
            </div>
          );
        })}
      </div>
      <div className="flex justify-center mt-4">
        <button
          className="bg-gray-300 hover:bg-gray-400 text-sm px-4 py-1 rounded-full"
          onClick={loadUnlocked}
        >
          Refresh
        </button>
      </div>

      {selected && (
        <ComplimentModal
          data={selected}
          onClose={() => setSelected(null)}
        />
      )}
    </div>
  );
}
