import React, { useEffect, useState, useRef } from 'react';
import { motion, useAnimation } from 'framer-motion';

const compliments = [
  { text: '1 Humble', image: '/stars/pink.png' },
  { text: '2 Sincerity', image: '/stars/blue.png' },
  { text: '3 Patient', image: '/stars/purple.png' },
  { text: '4 smart', image: '/stars/yellow.png' },
  { text: '5 愈挫愈強', image: '/stars/green.png' },
];

const Star = ({ src, x, y }) => (
  <motion.img
    src={src}
    alt="star"
    className="absolute w-8 h-8 pointer-events-none"
    initial={{ x: x - 16, y: y - 16, scale: 0 }}
    animate={{ x: x - 16, y: y - 16, scale: [0, 1.2, 1] }}
    transition={{ duration: 0.4, ease: 'easeOut' }}
  />
);

export default function StarJar() {
  const [stars, setStars] = useState([]);
  const [wish, setWish] = useState('');
  const [usedCompliments, setUsedCompliments] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem('unlockedCompliments') || '[]');
    setUsedCompliments(saved);
  }, []);

  const spawnStars = () => {
    setStars(compliments);
    setWish('');
  };

  const makeWish = () => {
    if (stars.length === 0) return;
    const idx = Math.floor(Math.random() * stars.length);
    const picked = stars[idx];
    const updatedUsed = [...usedCompliments, picked];
    localStorage.setItem('unlockedCompliments', JSON.stringify(updatedUsed));
    setUsedCompliments(updatedUsed);
    setStars(prev => prev.filter((_, i) => i !== idx));
    setWish(picked.text);
  };

  return (
    <div className="flex flex-col items-center mt-10">
      <div className="relative w-[300px] h-[300px]">
        <img src="/jar/back.png" alt="jar back" className="absolute inset-0 w-full h-full object-contain z-0" />
        {stars.map(({ image, text }, index) => (
          <Star key={index} src={image} x={150 + (index * 30) % 90} y={100 + (index * 25) % 60} />
        ))}
        <img src="/jar/lid.png" alt="jar lid" className="absolute inset-0 w-full h-full object-contain z-10" />
      </div>

      <div className="flex gap-4 mt-6">
        <motion.button
          className="bg-pink-200 hover:bg-pink-300 px-4 py-2 rounded-full shadow text-white font-semibold text-sm"
          onClick={spawnStars}
        >
          Shake me
        </motion.button>

        <motion.button
          className="bg-gradient-to-r from-pink-300 to-purple-300 hover:from-pink-400 hover:to-purple-400 px-6 py-3 rounded-full shadow-lg text-white font-semibold text-lg disabled:opacity-50"
          onClick={makeWish}
          disabled={stars.length === 0}
        >
          Make a wish! ✨
        </motion.button>
      </div>

      <div className="mt-7 text-center h-24 flex items-center justify-center">
        {wish && <p className="text-pink-700 text-xl font-semibold">✨ {wish} ✨</p>}
      </div>
    </div>
  );
}
