import React, { useEffect, useState, useRef } from 'react';
import { motion, useAnimation } from 'framer-motion';

const compliments = [
  { text: '1 Humble', image: '/stars/pink.png' },
  { text: '2 Sincerity', image: '/stars/blue.png' },
  { text: '3 Patient', image: '/stars/purple.png' },
  { text: '4 smart', image: '/stars/yellow.png' },
  { text: '5 愈挫愈強', image: '/stars/green.png' },
];

const maskImageSrc = '/jar/spawn-mask.png';

const createMaskContext = () => {
  return new Promise((resolve) => {
    const img = new Image();
    img.crossOrigin = 'anonymous';
    img.src = maskImageSrc;
    img.onload = () => {
      const canvas = document.createElement('canvas');
      canvas.width = 300;
      canvas.height = 300;
      const ctx = canvas.getContext('2d');
      if (ctx) ctx.drawImage(img, 0, 0, 300, 300);
      resolve(ctx);
    };
    img.onerror = () => resolve(null);
  });
};

const getNonOverlappingPosition = async (existing, radius = 32) => {
  const ctx = await createMaskContext();
  if (!ctx) return { x: 150, y: 180 };
  const halfSize = radius / 2;
  let attempts = 0;
  while (attempts < 1000) {
    const x = Math.floor(Math.random() * 300);
    const y = Math.floor(Math.random() * 300);
    const corners = [
      { x: x - halfSize, y: y - halfSize },
      { x: x + halfSize, y: y - halfSize },
      { x: x - halfSize, y: y + halfSize },
      { x: x + halfSize, y: y + halfSize }
    ];
    const allValid = corners.every(({ x, y }) => {
      if (x < 0 || x >= 300 || y < 0 || y >= 300) return false;
      const pixel = ctx.getImageData(x, y, 1, 1).data;
      return pixel[3] >= 50;
    });
    const notTooClose = existing.every((pos) => {
      const dx = pos.x - x;
      const dy = pos.y - y;
      return dx * dx + dy * dy > radius * radius;
    });
    if (allValid && notTooClose) return { x, y };
    attempts++;
  }
  return { x: 150, y: 180 };
};

const Star = React.forwardRef(({ src, x, y }, ref) => {
  const controls = useAnimation();
  useEffect(() => {
    controls.start({
      x: x - 16,
      y: y - 16,
      scale: [0, 1.2, 1],
      transition: { duration: 0.5, ease: "easeOut" }
    });
  }, [x, y]);

  return (
    <motion.img
      ref={ref}
      src={src}
      alt="star"
      className="absolute w-8 h-8 pointer-events-none"
      animate={controls}
      initial={{ x: x - 16, y: y - 16, scale: 0 }}
      style={{ zIndex: 5 }}
    />
  );
});

export default function StarJar() {
  const [stars, setStars] = useState([]);
  const [hasSpawned, setHasSpawned] = useState(false);
  const [wish, setWish] = useState('');
  const [showVideo, setShowVideo] = useState(false);
  const starRefs = useRef([]);

  useEffect(() => {
    const savedStars = JSON.parse(localStorage.getItem('jarStars') || '[]');
    const used = JSON.parse(localStorage.getItem('unlockedCompliments') || '[]');
    if (savedStars.length > 0) {
      setStars(savedStars);
      setHasSpawned(true);
      starRefs.current = savedStars.map(() => React.createRef());
    }
  }, []);

  const spawnStars = async () => {
    const generated = [];
    for (let i = 0; i < compliments.length; i++) {
      const pos = await getNonOverlappingPosition(generated);
      generated.push({
        id: `star-${i}`,
        src: compliments[i].image,
        x: pos.x,
        y: pos.y,
        compliment: compliments[i].text
      });
    }
    setStars(generated);
    starRefs.current = generated.map(() => React.createRef());
    setHasSpawned(true);
    setWish('');
    setShowVideo(false);
    localStorage.setItem('jarStars', JSON.stringify(generated));
  };

  const makeWish = async () => {
    if (!stars.length) return;
    const index = Math.floor(Math.random() * stars.length);
    const selected = stars[index];
    const remaining = stars.filter((_, i) => i !== index);
    setStars(remaining);
    starRefs.current = starRefs.current.filter((_, i) => i !== index);
    localStorage.setItem('jarStars', JSON.stringify(remaining));
    const prev = JSON.parse(localStorage.getItem('unlockedCompliments') || '[]');
    const updated = Array.from(new Set([...prev, selected.compliment]));
    localStorage.setItem('unlockedCompliments', JSON.stringify(updated));
    setWish(selected.compliment);
    setShowVideo(true);
  };

  const resetAll = () => {
    localStorage.removeItem('jarStars');
    localStorage.removeItem('unlockedCompliments');
    window.location.reload();
  };

  return (
    <div className="flex flex-col items-center mt-10">
      <div className="relative w-[300px] h-[300px]">
        <img src="/jar/back.png" alt="jar back" className="absolute inset-0 w-full h-full object-contain z-0" />
        {stars.map((star, i) => (
          <Star key={star.id} src={star.src} x={star.x} y={star.y} ref={starRefs.current[i]} />
        ))}
        <img src="/jar/lid.png" alt="jar lid" className="absolute inset-0 w-full h-full object-contain z-10" />
      </div>

      <div className="flex gap-4 mt-6">
        {!hasSpawned ? (
          <motion.button
            className="bg-gradient-to-r from-pink-300 to-purple-300 px-6 py-3 rounded-full shadow-lg text-white font-semibold text-lg"
            onClick={spawnStars}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Press me!
          </motion.button>
        ) : (
          <>
            <motion.button
              className="bg-gradient-to-r from-pink-300 to-purple-300 px-6 py-3 rounded-full shadow-lg text-white font-semibold text-lg"
              onClick={makeWish}
              disabled={stars.length === 0}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Make a wish! ✨
            </motion.button>
            <motion.button
              className="bg-gray-300 px-4 py-2 rounded-full text-sm"
              onClick={resetAll}
            >
              Reset
            </motion.button>
          </>
        )}
      </div>

      <div className="mt-7 text-center h-24 flex items-center justify-center">
        {showVideo ? (
          <video
            playsInline autoPlay muted
            onEnded={() => setShowVideo(false)}
            className="w-49 h-36 object-contain"
          >
            <source src="/stars/unravel.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        ) : wish ? (
          <p className="text-pink-700 text-xl font-semibold">✨ {wish} ✨</p>
        ) : null}
      </div>
    </div>
  );
}
