import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const compliments = [
  { text: 'Kind', image: '/stars/kind.png', description: 'You bring warmth to everyone around you.' },
  { text: 'Creative', image: '/stars/creative.png', description: 'Your imagination knows no bounds.' },
  // ... other compliments
];

const StarJar = () => {
  const [hasSpawned, setHasSpawned] = useState(false);
  const [stars, setStars] = useState([]);
  const [wish, setWish] = useState(null);
  const [selectedCompliment, setSelectedCompliment] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showPasswordPrompt, setShowPasswordPrompt] = useState(false);
  const [passwordInput, setPasswordInput] = useState('');
  const [activeTab, setActiveTab] = useState('jar');

  const spawnStars = () => {
    setHasSpawned(true);
    setStars(compliments);
  };

  const makeWish = () => {
    if (stars.length === 0) return;
    const randomIndex = Math.floor(Math.random() * stars.length);
    const selected = stars[randomIndex];
    setWish(selected.text);
  };

  const resetJar = () => {
    setHasSpawned(false);
    setStars([]);
    setWish(null);
    setSelectedCompliment(null);
  };

  return (
    <div className="min-h-screen bg-white relative">
      {/* Tabs */}
      <div className="flex justify-center gap-4 pt-4">
        <button
          onClick={() => setActiveTab('jar')}
          className={`px-5 py-3 rounded-full text-sm font-medium transition duration-150 ease-in-out ${
            activeTab === 'jar'
              ? 'bg-[#E4DAF2] text-[#6B5F84] shadow-sm'
              : 'bg-[#F2F2F2] text-[#999] hover:bg-[#e6e6e6]'
          }`}
        >
          Star Jar
        </button>
        <button
          onClick={() => setActiveTab('dex')}
          className={`px-5 py-3 rounded-full text-sm font-medium transition duration-150 ease-in-out ${
            activeTab === 'dex'
              ? 'bg-[#E4DAF2] text-[#6B5F84] shadow-sm'
              : 'bg-[#F2F2F2] text-[#999] hover:bg-[#e6e6e6]'
          }`}
        >
          Star Dex
        </button>
      </div>

      {/* Compliment Output */}
      {!hasSpawned ? (
        <div className="flex justify-center mt-10">
          <motion.button
            className="bg-gradient-to-r from-[#e0ccff] to-[#c8e3f9] hover:from-[#d9c8ff] hover:to-[#bfe2f7] text-white text-lg px-6 py-3 rounded-full shadow-xl font-semibold transition hover:scale-105 duration-200 ease-out backdrop-blur-sm border border-white/30"
            onClick={spawnStars}
          >
            Press me!
          </motion.button>
        </div>
      ) : (
        <div className="flex justify-center mt-6 gap-4 flex-wrap">
          <motion.button
            className="bg-white/30 backdrop-blur-md border border-[#cde0f0] text-sm text-[#444] px-4 py-2 rounded-full shadow-sm hover:bg-white/50 hover:scale-105 transition"
            onClick={makeWish}
            disabled={stars.length === 0}
          >
            Make a wish! âœ¨
          </motion.button>
        </div>
      )}

      {/* Compliment Display */}
      {!hasSpawned || !wish ? null : (
        <div className="text-center mt-8">
          <p className="text-[#6D679D] text-xl font-semibold">âœ¨ {wish} âœ¨</p>
          <button
            onClick={() => {
              const complimentObj = compliments.find(c => c.text === wish);
              setSelectedCompliment({
                text: complimentObj?.text || wish,
                image: complimentObj?.image || '/stars/default.png',
                description:
                  complimentObj?.description ||
                  'This star reflects one of your amazing qualities ðŸ’« Keep collecting more!',
                locked: false,
              });
              setShowModal(true);
            }}
            className="mt-1 px-3 py-1 rounded-full text-sm bg-white text-[#6D679D] border border-[#d6d2e8] hover:bg-[#f3f0fa] transition"
          >
            Read more
          </button>
        </div>
      )}

      {/* Compliment Modal */}
      {showModal && selectedCompliment && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 z-50">
          <div className="bg-white rounded-lg p-6 w-80 shadow-xl">
            <h3 className="text-xl font-bold text-center text-[#7E6BAA] mb-2">{selectedCompliment.text}</h3>
            <img src={selectedCompliment.image} alt="star" className="mx-auto mb-4 w-20 h-20" />
            <p className="text-sm text-center text-gray-600">{selectedCompliment.description}</p>
            <button
              onClick={() => setShowModal(false)}
              className="mt-4 px-4 py-2 bg-[#E4DAF2] text-[#6B5F84] rounded-full text-sm font-medium hover:bg-[#d9d1ec] transition"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Password-Protected Reset Button */}
      <div className="fixed bottom-6 right-6 z-40">
        <button
          onClick={() => setShowPasswordPrompt(true)}
          className="px-3 py-1 rounded-full text-sm bg-red-100 text-red-600 border border-red-300 hover:bg-red-200 transition"
        >
          Reset Jar
        </button>
      </div>

      {/* Password Prompt Modal */}
      {showPasswordPrompt && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-80">
            <h3 className="text-lg font-semibold mb-4 text-center">Enter Password to Reset</h3>
            <input
              type="password"
              value={passwordInput}
              onChange={(e) => setPasswordInput(e.target.value)}
              className="w-full px-3 py-2 border rounded-md mb-4"
              placeholder="Password"
            />
            <div className="flex justify-end gap-2">
              <button
                className="px-4 py-2 bg-gray-200 rounded-md text-sm"
                onClick={() => setShowPasswordPrompt(false)}
              >
                Cancel
              </button>
              <button
                className="px-4 py-2 bg-red-500 text-white rounded-md text-sm"
                onClick={() => {
                  if (passwordInput === '1234') {
                    resetJar();
                    setShowPasswordPrompt(false);
                    setPasswordInput('');
                  } else {
                    alert('Incorrect password!');
                  }
                }}
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StarJar;

function ResetConfirmationModal({ onClose, onConfirm }) {
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleConfirm = () => {
    if (password === '1234') {
      onConfirm();
      setPassword('');
      setError('');
    } else {
      setError('Incorrect password. Try again.');
    }
  };

  return (
    <div className="fixed inset-0 bg-black/30 backdrop-blur-sm flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 shadow-lg w-72">
        <h3 className="text-lg font-semibold text-gray-800 mb-2">Confirm Reset</h3>
        <p className="text-sm text-gray-600 mb-4">
          Please enter the password to reset the jar.
        </p>
        <input
          type="password"
          placeholder="Enter password"
          value={password}
          onChange={(e) => {
            setPassword(e.target.value);
            setError('');
          }}
          className="w-full px-3 py-2 mb-2 border rounded-md text-sm focus:outline-none focus:ring focus:border-[#C8D7F9]"
        />
        {error && <p className="text-red-500 text-xs mb-2">{error}</p>}
        <div className="flex justify-end gap-2">
          <button
            className="text-sm text-gray-500 hover:underline"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            onClick={handleConfirm}
            className="text-sm bg-[#E4DAF2] text-[#6B5F84] px-3 py-1 rounded-md hover:bg-[#d5cfee]"
          >
            Confirm
          </button>
        </div>
      </div>
    </div>
  );
}
