// src/ComplimentDex.jsx
import React, { useEffect, useMemo, useState } from 'react';
import compliments from './complimentData';

// --- MUST match StarJar.js ---
const BUILD_VERSION = '2025-08-11-1'; // keep in sync with StarJar.js
const asset = (p) =>
  `${process.env.PUBLIC_URL}${p.startsWith('/') ? p : `/${p}`}?v=${BUILD_VERSION}`;

const APP_NS = (process.env.PUBLIC_URL || '') + ':starjar';
const KEYS = {
  stars: `${APP_NS}:jarStars`,
  unlocked: `${APP_NS}:unlockedCompliments`,
  ver: `${APP_NS}:version`,
};
// --- end shared bits ---

export default function ComplimentDex() {
  const [unlocked, setUnlocked] = useState(new Set());

  const loadUnlocked = () => {
    try {
      const arr = JSON.parse(localStorage.getItem(KEYS.unlocked) || '[]');
      setUnlocked(new Set(Array.isArray(arr) ? arr : []));
    } catch {
      setUnlocked(new Set());
    }
  };

  useEffect(() => {
    loadUnlocked();

    const reload = () => loadUnlocked();

    // Intra-app sync (from StarJar)
    window.addEventListener('starjar:updated', reload);

    // Cross-tab sync
    const onStorage = (e) => {
      if (e.key === KEYS.unlocked) loadUnlocked();
    };
    window.addEventListener('storage', onStorage);

    // Refresh when returning to the tab
    const onVisible = () => {
      if (document.visibilityState === 'visible') loadUnlocked();
    };
    document.addEventListener('visibilitychange', onVisible);

    return () => {
      window.removeEventListener('starjar:updated', reload);
      window.removeEventListener('storage', onStorage);
      document.removeEventListener('visibilitychange', onVisible);
    };
  }, []);

  const items = useMemo(() => {
    // Decorate with unlocked flag and keep a cute sort: unlocked first
    const withFlags = compliments.map((c) => ({
      ...c,
      unlocked: unlocked.has(c.text),
    }));
    return withFlags.sort((a, b) => Number(b.unlocked) - Number(a.unlocked));
  }, [unlocked]);

  return (
    <div className="max-w-xl mx-auto px-4 pt-4 pb-12">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-semibold text-[#6D679D]">ComplimentDex</h2>
        <button
          onClick={loadUnlocked}
          className="text-sm px-3 py-1 rounded-full border border-[#d6d2e8] bg-white hover:bg-[#f3f0fa] transition"
        >
          Refresh
        </button>
      </div>

      <div className="grid grid-cols-3 sm:grid-cols-4 gap-4">
        {items.map((c) => {
          const displayName = c.unlocked ? c.text : 'XXX';
          return (
            <div
              key={c.text}
              className={`relative rounded-xl p-3 border shadow-sm ${
                c.unlocked ? 'bg-white' : 'bg-gray-50'
              }`}
            >
              <div className="w-14 h-14 mx-auto relative">
                <img
                  src={asset(c.image)}
                  alt={c.text}
                  className={`w-full h-full object-contain ${
                    c.unlocked ? '' : 'opacity-60 saturate-0 blur-[0.5px]'
                  }`}
                  onError={(e) => {
                    e.currentTarget.onerror = null;
                    e.currentTarget.src = asset('/stars/default.PNG'); // ensure this exists
                  }}
                />

                {!c.unlocked && (
                  // Tiny, tasteful corner badge (inline SVG lock)
                  <span
                    className="absolute top-1 right-1 inline-flex items-center justify-center
                               w-4 h-4 rounded-full border border-gray-200 bg-white/85"
                    aria-label="locked"
                    title="Locked"
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 24 24"
                      fill="currentColor"
                      className="w-3 h-3 text-gray-500"
                      aria-hidden="true"
                    >
                      <path d="M12 1a5 5 0 00-5 5v3H6a2 2 0 00-2 2v8a2 2 0 002 2h12a2 2 0 002-2v-8a2 2 0 00-2-2h-1V6a5 5 0 00-5-5zm3 8V6a3 3 0 10-6 0v3h6z" />
                    </svg>
                  </span>
                )}
              </div>

              <div className="mt-2 text-center">
                <div className="text-xs font-medium text-[#6D679D]">{displayName}</div>
                {c.unlocked ? (
                  <div className="text-[11px] text-gray-500 line-clamp-2 mt-1">
                    {c.description}
                  </div>
                ) : (
                  <div className="text-[11px] text-gray-400 mt-1">Collect this star to reveal</div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      <p className="text-center text-xs text-gray-500 mt-4">
        {unlocked.size}/{compliments.length} collected
      </p>
    </div>
  );
}
