import React, { useEffect, useState } from 'react';
import { motion, useAnimation } from 'framer-motion';

const starImages = [
  '/stars/1.png',
  '/stars/2.png',
  '/stars/3.png',
  '/stars/4.png',
  '/stars/5.png'
];

const maskPath = '/jar/spawn-mask.png'; 

const getRandomPositionFromMask = async () => {
  return new Promise((resolve) => {
    const img = new Image();
    img.crossOrigin = "anonymous";
    img.src = '/lovable-uploads/3956bece-3377-40c4-8fd6-3680bf51423d.png'; // Mask image

    img.onload = () => {
      const canvas = document.createElement("canvas");
      canvas.width = 300; // Match jar/container size
      canvas.height = 300;

      const ctx = canvas.getContext("2d");
      ctx.drawImage(img, 0, 0, 300, 300);

      let attempts = 0;
      let x, y, pixel;

      do {
        x = Math.floor(Math.random() * 300);
        y = Math.floor(Math.random() * 300);

        try {
          pixel = ctx.getImageData(x, y, 1, 1).data;
        } catch (error) {
          console.log('Using fallback position (image data read failed)');
          resolve({ x: 150, y: 180 }); // Fallback to center
          return;
        }

        attempts++;
        if (attempts > 1000) {
          console.log('Using fallback position (too many attempts)');
          resolve({ x: 150, y: 180 }); // Fallback to center
          return;
        }
      } while (pixel[3] < 50); // Only accept areas with alpha > 50

      resolve({ x, y });
    };

    img.onerror = () => {
      console.log('Using fallback position (image failed to load)');
      resolve({ x: 150, y: 180 }); // Fallback to center
    };
  });
};


const Star = ({ src, x, y, delay = 0 }) => {
  const controls = useAnimation();

  const shake = async () => {
    // Initial gentle spawn animation
    await controls.start({
      scale: [0, 1.2, 1],
      rotate: [0, 180],
      transition: { duration: 0.6, ease: "easeOut" }
    });

    // Shake animation - constrain movement to stay within bounds
    for (let i = 0; i < 8; i++) {
      const shakeX = x + (Math.random() - 0.5) * 20; // Reduced shake range
      const shakeY = y + (Math.random() - 0.5) * 20;

      // Ensure shake doesn't go outside container
      const constrainedX = Math.max(16, Math.min(284, shakeX));
      const constrainedY = Math.max(16, Math.min(284, shakeY));

      await controls.start({
        x: constrainedX,
        y: constrainedY,
        rotate: (Math.random() - 0.5) * 60,
        transition: { duration: 0.08 },
      });
    }

    // Return to original position
    await controls.start({ 
      x, 
      y, 
      rotate: 0,
      transition: { duration: 0.4, ease: "easeOut" }
    });
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      shake();
    }, delay);

    return () => clearTimeout(timer);
  }, [x, y, delay]);

  return (
    <motion.img
      src={src}
      alt="star"
      className="absolute w-8 h-8 pointer-events-none"
      animate={controls}
      initial={{ x: x - 16, y: y - 16, scale: 0 }} // Center the star and start scaled down
      style={{ zIndex: 5 }}
    />
  );
};

export default function StarJar() {
  const [stars, setStars] = useState([]);
  const [shakeCount, setShakeCount] = useState(0);
  const [isShaking, setIsShaking] = useState(false);

  useEffect(() => {
    const generateStars = async () => {
      const generated = [];
      for (let i = 0; i < 5; i++) {
        const pos = await getRandomPositionFromMask(maskPath);
        generated.push({
          id: `${shakeCount}-${i}`, // Unique key for re-renders
          src: starImages[i % starImages.length],
          x: pos.x,
          y: pos.y,
          delay: i * 100, // Stagger the animations
        });
      }
      setStars(generated);
    };

    generateStars();
  }, [shakeCount]);

  const handleShake = async () => {
    if (isShaking) return;

    setIsShaking(true);
    setShakeCount((prev) => prev + 1);

    // Reset shaking state after animation completes
    setTimeout(() => {
      setIsShaking(false);
    }, 2000);
  };

  return (
    <div className="flex flex-col items-center mt-10">
      {/* Jar container */}
      <div className="relative w-[300px] h-[300px]">
        {/* back of jar */}
        <img 
          src="/jar/back.png" 
          alt="jar back" 
          className="absolute inset-0 w-full h-full object-contain z-0" 
        />

        {/* stars */}
        {stars.map((star) => (
          <Star
            key={star.id}
            src={star.src}
            x={star.x}
            y={star.y}
            delay={star.delay}
          />
        ))}

        {/* front rim of jar */}
        <img 
          src="/jar/front.png" 
          alt="jar front" 
          className="absolute inset-0 w-full h-full object-contain z-10" 
        />
      </div>

      {/* Shake button */}
      <motion.button
        className="mt-8 bg-gradient-to-r from-pink-300 to-purple-300 hover:from-pink-400 hover:to-purple-400 px-8 py-3 rounded-full shadow-lg text-white font-semibold text-lg disabled:opacity-50 disabled:cursor-not-allowed"
        onClick={handleShake}
        disabled={isShaking}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        {isShaking ? "Shaking..." : "Shake the Jar! ✨"}
      </motion.button>

      <p className="mt-4 text-gray-600 text-center max-w-md">
        Click the button to shake the jar and watch the magical stars dance around!
      </p>
    </div>
  );  
}
