import React, { useEffect, useState, useRef } from 'react';
import { motion, useAnimation } from 'framer-motion';

const starImages = [
  '/stars/1.png',
  '/stars/2.png',
  '/stars/3.png',
  '/stars/4.png',
  '/stars/5.png'
];

const compliments = [
  "1 Humble",
  "2 Sincerity",
  "3 Patient",
  "4 smart",
  "5 愈挫愈強"
];

const getRandomPositionFromMask = () => {
  return new Promise((resolve) => {
    const img = new Image();
    img.crossOrigin = "anonymous";
    img.src = '/jar/spawn-mask.png';

    img.onload = () => {
      const canvas = document.createElement("canvas");
      canvas.width = 300;
      canvas.height = 300;
      const ctx = canvas.getContext("2d");
      ctx.drawImage(img, 0, 0, 300, 300);

      let attempts = 0;
      let x, y;
      const starSize = 32;
      const halfSize = starSize / 2;
      let allCornersValid = false;

      do {
        x = Math.floor(Math.random() * 300);
        y = Math.floor(Math.random() * 300);

        const topLeft = { x: x - halfSize, y: y - halfSize };
        const topRight = { x: x + halfSize, y: y - halfSize };
        const bottomLeft = { x: x - halfSize, y: y + halfSize };
        const bottomRight = { x: x + halfSize, y: y + halfSize };

        const corners = [topLeft, topRight, bottomLeft, bottomRight];
        allCornersValid = true;

        try {
          for (const corner of corners) {
            if (corner.x < 0 || corner.x >= 300 || corner.y < 0 || corner.y >= 300) {
              allCornersValid = false;
              break;
            }
            const pixel = ctx.getImageData(corner.x, corner.y, 1, 1).data;
            if (pixel[3] < 50) {
              allCornersValid = false;
              break;
            }
          }
        } catch (error) {
          resolve({ x: 150, y: 180 });
          return;
        }

        attempts++;
        if (attempts > 1000) {
          resolve({ x: 150, y: 180 });
          return;
        }
      } while (!allCornersValid);

      resolve({ x, y });
    };

    img.onerror = () => {
      resolve({ x: 150, y: 180 });
    };
  });
};

const Star = React.forwardRef(({ src, x, y, updateFinalPosition }, ref) => {
  const controls = useAnimation();
  let finalX = x;
  let finalY = y;

  React.useImperativeHandle(ref, () => ({
    async shake() {
      for (let i = 0; i < 8; i++) {
        const shakeX = finalX + (Math.random() - 0.5) * 20;
        const shakeY = finalY + (Math.random() - 0.5) * 20;
        const constrainedX = Math.max(16, Math.min(284, shakeX));
        const constrainedY = Math.max(16, Math.min(284, shakeY));

        await controls.start({
          x: constrainedX,
          y: constrainedY,
          rotate: (Math.random() - 0.5) * 60,
          transition: { duration: 0.08 },
        });

        finalX = constrainedX;
        finalY = constrainedY;
      }
      await controls.start({
        x: finalX,
        y: finalY,
        rotate: 0,
        transition: { duration: 0.4, ease: "easeOut" }
      });

      updateFinalPosition(finalX, finalY);
    }
  }));

  return (
    <motion.img
      src={src}
      alt="star"
      className="absolute w-8 h-8 pointer-events-none"
      animate={controls}
      initial={{ x: x - 16, y: y - 16, scale: 1 }}
      style={{ zIndex: 5 }}
    />
  );
});

export default function StarJar() {
  const [stars, setStars] = useState([]);
  const [hasSpawned, setHasSpawned] = useState(false);
  const [wish, setWish] = useState("");
  const [isRevealing, setIsRevealing] = useState(false);
  const starRefs = useRef([]);

  const spawnStars = async () => {
    const generated = [];
    for (let i = 0; i < 5; i++) {
      const pos = await getRandomPositionFromMask();
      generated.push({
        id: `star-${i}`,
        src: starImages[i % starImages.length],
        x: pos.x,
        y: pos.y
      });
    }
    setStars(generated);
    setHasSpawned(true);
  };

  const updateStarPosition = (index, x, y) => {
    setStars(prev => {
      const updated = [...prev];
      updated[index] = { ...updated[index], x, y };
      return updated;
    });
  };

  const shakeStars = async () => {
    setWish("");
    setIsRevealing(true);
    await Promise.all(
      starRefs.current.map((ref) => ref?.shake?.())
    );
    setTimeout(() => {
      const randomWish = compliments[Math.floor(Math.random() * compliments.length)];
      setWish(randomWish);
      setIsRevealing(false);
    }, 1500);
  };

  return (
    <div className="flex flex-col items-center mt-10">
      <div className="relative w-[300px] h-[300px]">
        <img src="/jar/back.png" alt="jar back" className="absolute inset-0 w-full h-full object-contain z-0" />

        {stars.map((star, i) => (
          <Star
            key={star.id}
            src={star.src}
            x={star.x}
            y={star.y}
            ref={(el) => (starRefs.current[i] = el)}
            updateFinalPosition={(x, y) => updateStarPosition(i, x, y)}
          />
        ))}

        <img src="/jar/front.png" alt="jar front" className="absolute inset-0 w-full h-full object-contain z-10" />
      </div>

      <motion.button
        className="mt-8 bg-gradient-to-r from-pink-300 to-purple-300 hover:from-pink-400 hover:to-purple-400 px-8 py-3 rounded-full shadow-lg text-white font-semibold text-lg"
        onClick={hasSpawned ? shakeStars : spawnStars}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        {hasSpawned ? "Make a wish! ✨" : "Press me!"}
      </motion.button>

      <div className="mt-4 text-center h-24 flex items-center justify-center">
        {isRevealing && (
          <img
            src="/animations/unravel.gif"
            alt="unraveling..."
            className="w-32 h-20 object-contain"
          />
        )}
        {!isRevealing && wish && (
          <p className="text-pink-700 text-xl font-semibold">✨ {wish} ✨</p>
        )}
      </div>
    </div>
  );
}
