import React, { useEffect, useState } from 'react';
import { motion, useAnimation } from 'framer-motion';

// Image sources
const starImages = [
  '/stars/1.png',
  '/stars/2.png',
  '/stars/3.png',
  '/stars/4.png',
  '/stars/5.png'
];

// Load mask image and return a valid x/y coordinate in the white/red area
const getRandomPositionFromMask = async (maskPath) => {
  return new Promise((resolve) => {
    const img = new Image();
    img.src = maskPath;

    img.onload = () => {
      const canvas = document.createElement("canvas");
      canvas.width = img.width;
      canvas.height = img.height;

      const ctx = canvas.getContext("2d");
      ctx.drawImage(img, 0, 0);

      const { width, height } = canvas;
      let x, y, pixel;

      do {
        x = Math.floor(Math.random() * width);
        y = Math.floor(Math.random() * height);
        pixel = ctx.getImageData(x, y, 1, 1).data;
      } while (pixel[3] < 100); // Look for opaque pixels

      // Return coordinates relative to top-left corner (CSS-style positioning)
      resolve({ x, y });
    };
  });
};


const Star = ({ src, initialX, initialY }) => {
  const controls = useAnimation();

  const shake = async () => {
    for (let i = 0; i < 5; i++) {
      await controls.start({
        x: initialX + Math.random() * 20 - 10,
        y: initialY + Math.random() * 20 - 10,
        transition: { duration: 0.1 }
      });
    }
    await controls.start({ x: initialX, y: initialY });
  };

  useEffect(() => {
    shake();
  }, []); // shake only on mount

  return (
    <motion.img
      src={src}
      alt=""
      className="absolute w-12 h-12"
      animate={controls}
      initial={{ x: initialX, y: initialY }}
    />
  );
};

export default function StarJar() {
  const [stars, setStars] = useState([]);
  const [shakeCount, setShakeCount] = useState(0);

  useEffect(() => {
    const generateStars = async () => {
      const generated = [];
      for (let i = 0; i < 5; i++) {
        const pos = await getRandomPositionFromMask("/jar/spawn-mask.png");
        generated.push({
          src: starImages[i % starImages.length],
          x: pos.x,
          y: pos.y
        });
      }
      setStars(generated);
    };
    generateStars();
  }, [shakeCount]);

  const handleShake = () => {
    setShakeCount((prev) => prev + 1);
  };

  return (
    <div className="relative w-[300px] h-[300px] mx-auto mt-10">
      {/* Back of jar */}
      <img src="/jar/back.png" alt="" className="absolute z-0" />

      {/* Stars */}
      {stars.map((star, i) => (
        <Star key={i} src={star.src} initialX={star.x} initialY={star.y} />
      ))}

      {/* Front of jar */}
      <img src="/jar/front.png" alt="" className="absolute z-10" />

      {/* Shake Button */}
      <button
        className="absolute bottom-[-60px] left-1/2 -translate-x-1/2 bg-purple-300 px-4 py-2 rounded-full shadow"
        onClick={handleShake}
      >
        Shake!
      </button>
    </div>
  );
}
