import React, { useEffect, useState, useRef } from 'react';
import { motion, useAnimation } from 'framer-motion';

const starImages = [
  '/stars/1.png',
  '/stars/2.png',
  '/stars/3.png',
  '/stars/4.png',
  '/stars/5.png'
];

const compliments = [
  '1 Humble',
  '2 Sincerity',
  '3 Patient',
  '4 smart',
  '5 遇挫越強'
];

const maskImageSrc = '/jar/spawn-mask.png';

const createMaskContext = () => {
  return new Promise((resolve) => {
    const img = new Image();
    img.crossOrigin = 'anonymous';
    img.src = maskImageSrc;

    img.onload = () => {
      const canvas = document.createElement('canvas');
      canvas.width = 300;
      canvas.height = 300;
      const ctx = canvas.getContext('2d');
      if (ctx) {
        ctx.drawImage(img, 0, 0, 300, 300);
      }
      resolve(ctx);
    };

    img.onerror = () => {
      resolve(null);
    };
  });
};

const getNonOverlappingPosition = async (existing, radius = 32) => {
  const ctx = await createMaskContext();
  if (!ctx) return { x: 150, y: 180 };

  const halfSize = radius / 2;
  let attempts = 0;

  while (attempts < 1000) {
    const x = Math.floor(Math.random() * 300);
    const y = Math.floor(Math.random() * 300);

    const corners = [
      { x: x - halfSize, y: y - halfSize },
      { x: x + halfSize, y: y - halfSize },
      { x: x - halfSize, y: y + halfSize },
      { x: x + halfSize, y: y + halfSize }
    ];

    const allValid = corners.every(({ x, y }) => {
      if (x < 0 || x >= 300 || y < 0 || y >= 300) return false;
      const pixel = ctx.getImageData(x, y, 1, 1).data;
      return pixel[3] >= 50;
    });

    const notTooClose = existing.every((pos) => {
      const dx = pos.x - x;
      const dy = pos.y - y;
      return dx * dx + dy * dy > radius * radius;
    });

    if (allValid && notTooClose) return { x, y };
    attempts++;
  }

  return { x: 150, y: 180 };
};

const Star = React.forwardRef(({ src, x, y, updateFinalPosition }, ref) => {
  const controls = useAnimation();
  let finalX = x;
  let finalY = y;

  useEffect(() => {
    controls.start({
      x: x - 16,
      y: y - 16,
      scale: [0, 1.2, 1],
      rotate: [0, 180, 0],
      transition: { duration: 0.6, ease: "easeOut" }
    });
  }, [x, y, controls]);

  React.useImperativeHandle(ref, () => ({
    async shake() {
      const ctx = await createMaskContext();
      if (!ctx) return;

      const starSize = 32;
      const halfSize = starSize / 2;

      for (let i = 0; i < 8; i++) {
        let newX = finalX + (Math.random() - 0.5) * 20;
        let newY = finalY + (Math.random() - 0.5) * 20;

        const corners = [
          { x: newX - halfSize, y: newY - halfSize },
          { x: newX + halfSize, y: newY - halfSize },
          { x: newX - halfSize, y: newY + halfSize },
          { x: newX + halfSize, y: newY + halfSize }
        ];

        const isValid = corners.every(({ x, y }) => {
          if (x < 0 || x >= 300 || y < 0 || y >= 300) return false;
          const pixel = ctx.getImageData(x, y, 1, 1).data;
          return pixel[3] >= 50;
        });

        if (!isValid) continue;

        await controls.start({
          x: newX - 16,
          y: newY - 16,
          rotate: (Math.random() - 0.5) * 60,
          transition: { duration: 0.08 }
        });

        finalX = newX;
        finalY = newY;
      }

      await controls.start({
        x: finalX - 16,
        y: finalY - 16,
        rotate: 0,
        transition: { duration: 0.4, ease: 'easeOut' }
      });

      updateFinalPosition(finalX, finalY);
    }
  }));

  return (
    <motion.img
      src={src}
      alt="star"
      className="absolute w-8 h-8 pointer-events-none"
      animate={controls}
      initial={{ x: x - 16, y: y - 16, scale: 0 }}
      style={{ zIndex: 5 }}
    />
  );
});

export default function StarJar() {
  const [stars, setStars] = useState([]);
  const [hasSpawned, setHasSpawned] = useState(false);
  const [wish, setWish] = useState('');
  const [showVideo, setShowVideo] = useState(false);
  const [usedCompliments, setUsedCompliments] = useState([]);
  const starRefs = useRef([]);

  const spawnStars = async () => {
    const generated = [];
    for (let i = 0; i < 5; i++) {
      const pos = await getNonOverlappingPosition(generated);
      generated.push({
        id: `star-${i}`,
        src: starImages[i % starImages.length],
        x: pos.x,
        y: pos.y
      });
    }
    setStars(generated);
    setHasSpawned(true);
    setWish('');
    setShowVideo(false);

    // create refs for each star
    starRefs.current = generated.map(() => React.createRef());

  };

  const updateStarPosition = (index, x, y) => {
    setStars((prev) => {
      const updated = [...prev];
      updated[index] = { ...updated[index], x, y };
      return updated;
    });
  };

  const shakeStars = async () => {
    if (!hasSpawned) return;
    await Promise.all(
      starRefs.current.map(ref => ref.current?.shake?.())
    );
  };

  const makeWish = async () => {
    if (!hasSpawned) return;
    setShowVideo(true);
    setWish('');
    await shakeStars();
  };

  const handleVideoEnd = () => {
    setShowVideo(false);
    const available = compliments.filter(c => !usedCompliments.includes(c));
    if (available.length === 0) {
      const random = compliments[Math.floor(Math.random() * compliments.length)];
      setUsedCompliments([random]);
      setWish(random);
    } else {
      const random = available[Math.floor(Math.random() * available.length)];
      setUsedCompliments(prev => [...prev, random]);
      setWish(random);
    }
  };

  return (
    <div className="flex flex-col items-center mt-10">
      <div className="relative w-[300px] h-[300px]">
        <img src="/jar/back.png" alt="jar back" className="absolute inset-0 w-full h-full object-contain z-0" />
        {stars.map((star, i) => (
          <Star
            key={star.id}
            src={star.src}
            x={star.x}
            y={star.y}
            ref={starRefs.current[i]}
            updateFinalPosition={(x, y) => updateStarPosition(i, x, y)}
          />
        ))}
        <img src="/jar/lid.png" alt="jar lid" className="absolute inset-0 w-full h-full object-contain z-10" />
      </div>

      <div className="flex gap-4 mt-6">
        {!hasSpawned ? (
          <motion.button
            className="bg-gradient-to-r from-pink-300 to-purple-300 hover:from-pink-400 hover:to-purple-400 px-6 py-3 rounded-full shadow-lg text-white font-semibold text-lg"
            onClick={spawnStars}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Press me!
          </motion.button>
        ) : (
          <>
            <motion.button
              className="bg-pink-200 hover:bg-pink-300 px-4 py-2 rounded-full shadow text-white font-semibold text-sm"
              onClick={shakeStars}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Shake me
            </motion.button>

            <motion.button
              className="bg-gradient-to-r from-pink-300 to-purple-300 hover:from-pink-400 hover:to-purple-400 px-6 py-3 rounded-full shadow-lg text-white font-semibold text-lg"
              onClick={makeWish}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Make a wish! ✨
            </motion.button>
          </>
        )}
      </div>

      <div className="mt-4 text-center h-24 flex items-center justify-center">
        {showVideo && (
          <video
            className="w-53 h-36 object-contain"
            autoPlay
            muted
            onEnded={handleVideoEnd}
          >
            <source src="/stars/unravel.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        )}
        {!showVideo && wish && (
          <p className="text-pink-700 text-xl font-semibold">✨ {wish} ✨</p>
        )}
      </div>
    </div>
  );
}
