import React, { useEffect, useState } from 'react';

const compliments = [
  { text: '1 Humble', image: '/stars/pink.png' },
  { text: '2 Sincerity', image: '/stars/blue.png' },
  { text: '3 Patient', image: '/stars/purple.png' },
  { text: '4 smart', image: '/stars/yellow.png' },
  { text: '5 愈挫愈強', image: '/stars/green.png' },
];

export default function ComplimentDex() {
  const [unlocked, setUnlocked] = useState([]);

  const loadFromStorage = () => {
    const saved = JSON.parse(localStorage.getItem('unlockedCompliments') || '[]');
    setUnlocked(saved);
  };

  useEffect(() => {
    loadFromStorage();

    const handleStorageChange = (e) => {
      if (e.key === 'unlockedCompliments') {
        loadFromStorage();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const resetDex = () => {
    localStorage.removeItem('unlockedCompliments');
    setUnlocked([]);
  };

  return (
    <div className="px-4 py-6">
      <h2 className="text-center text-xl font-bold text-pink-700 mb-6">Compliment 圖鑑</h2>

      <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-5 gap-4 justify-items-center mb-6">
        {compliments.map(({ text, image }, index) => {
          const isUnlocked = unlocked.includes(text);
          return (
            <div key={index} className="flex flex-col items-center text-center">
              <img
                src={image}
                alt={text}
                className={`w-12 h-12 sm:w-14 sm:h-14 md:w-16 md:h-16 transition-all duration-300 ${
                  isUnlocked ? '' : 'opacity-20 grayscale'
                }`}
              />
              <span className="mt-1 text-xs sm:text-sm font-medium text-gray-600">
                {isUnlocked ? text : '???'}
              </span>
            </div>
          );
        })}
      </div>

      <div className="flex justify-center gap-4">
        <button
          onClick={loadFromStorage}
          className="bg-pink-200 hover:bg-pink-300 px-4 py-2 rounded-full shadow text-white font-semibold text-sm"
        >
          Refresh
        </button>
        <button
          onClick={resetDex}
          className="bg-gray-300 hover:bg-gray-400 px-4 py-2 rounded-full shadow text-black font-medium text-sm"
        >
          Reset Dex
        </button>
      </div>
    </div>
  );
}
