import React from 'react';

const compliments = [
  { text: '1 Humble', image: '/stars/pink.png' },
  { text: '2 Sincerity', image: '/stars/blue.png' },
  { text: '3 Patient', image: '/stars/purple.png' },
  { text: '4 smart', image: '/stars/yellow.png' },
  { text: '5 愈挫愈強', image: '/stars/green.png' },
];

export default function ComplimentDex({ unlocked = [] }) {
  return (
    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 gap-4 p-6">
      {compliments.map((compliment, index) => {
        const isUnlocked = unlocked.includes(compliment.text);

        return (
          <div
            key={index}
            className={`flex flex-col items-center justify-center border-2 rounded-xl p-4 transition-all ${
              isUnlocked
                ? 'border-pink-400 bg-white'
                : 'border-gray-300 bg-gray-100 opacity-40'
            }`}
          >
            <img
              src={compliment.image}
              alt={`Compliment ${index + 1}`}
              className="w-12 h-12 mb-2"
            />
            <p className="text-sm text-center font-semibold">
              {isUnlocked ? compliment.text : '???'}
            </p>
          </div>
        );
      })}
    </div>
  );
}
