import React, { useState } from 'react';
import StarJar from './StarJar';
import ComplimentDex from './ComplimentDex';

export default function JarWithDex() {
  const [unlocked, setUnlocked] = useState([]);

  const handleUnlock = (complimentText) => {
    setUnlocked((prev) => {
      if (!prev.includes(complimentText)) {
        return [...prev, complimentText];
      }
      return prev;
    });
  };

  return (
    <>
      <StarJar onComplimentUnlocked={handleUnlock} />
      <h2 className="text-xl font-bold text-center mt-10 text-pink-700">ComplimentDex</h2>
      <ComplimentDex unlocked={unlocked} />
    </>
  );
}
