import React, { useEffect, useRef, useState } from 'react';
import { motion, useAnimation } from 'framer-motion';

const starImages = [
  '/stars/1.png',
  '/stars/2.png',
  '/stars/3.png',
  '/stars/4.png',
  '/stars/5.png'
];

const getRandomPosition = () => ({
  x: Math.random() * 200 - 100,
  y: Math.random() * 200 - 100
});

const Star = ({ src, initialX, initialY }) => {
  const controls = useAnimation();

  const shake = async () => {
    for (let i = 0; i < 5; i++) {
      await controls.start({
        x: initialX + Math.random() * 20 - 10,
        y: initialY + Math.random() * 20 - 10,
        transition: { duration: 0.1 }
      });
    }
    await controls.start({ x: initialX, y: initialY });
  };

  useEffect(() => {
    shake();
  }, []);

  return (
    <motion.img
      src={src}
      className="absolute w-12 h-12"
      animate={controls}
      initial={{ x: initialX, y: initialY }}
    />
  );
};

export default function StarJar() {
  const [stars, setStars] = useState([]);
  const [shakeCount, setShakeCount] = useState(0);

  useEffect(() => {
    // generate 5 stars with random initial positions
    const generatedStars = Array.from({ length: 5 }, (_, i) => {
      const pos = getRandomPosition();
      return {
        src: starImages[i % starImages.length],
        x: pos.x,
        y: pos.y
      };
    });
    setStars(generatedStars);
  }, [shakeCount]);

  const handleShake = () => {
    setShakeCount((prev) => prev + 1);
  };

  return (
    <div className="relative w-[300px] h-[300px] mx-auto mt-10">
      {/* back of jar */}
      <img src="/jar/back.png" className="absolute z-0" />

      {/* stars */}
      {stars.map((star, i) => (
        <Star
          key={i}
          src={star.src}
          initialX={star.x}
          initialY={star.y}
        />
      ))}

      {/* front jar (rim overlay) */}
      <img src="/jar/front.png" className="absolute z-10" />

      {/* shake button */}
      <button
        className="absolute bottom-[-60px] left-1/2 -translate-x-1/2 bg-purple-300 px-4 py-2 rounded-full shadow"
        onClick={handleShake}
      >
        Shake!
      </button>
    </div>
  );
}
